blockquote.pre:
[…]
  { b1 } -> ConvertibleTo<bool>;
  { !b1 } -> ConvertibleTo<bool>;
  { b1 &&  a } -> Same<bool>;
  { b1 ||  a } -> Same<bool>;
  { b1 && b2 } -> Same<bool>;
  { b1 &&  a } -> Same<bool>;
  {  a && b2 } -> Same<bool>;
  { b1 || b2 } -> Same<bool>;
  { b1 ||  a } -> Same<bool>;
  {  a || b2 } -> Same<bool>;
  { b1 == b2 } -> ConvertibleTo<bool>;
  { b1 ==  a } -> ConvertibleTo<bool>;
  {  a == b2 } -> ConvertibleTo<bool>;
  { b1 != b2 } -> ConvertibleTo<bool>;
  { b1 !=  a } -> ConvertibleTo<bool>;
  {  a != b2 } -> ConvertibleTo<bool>;
};
