blockquote.pre:
#include <chrono>
#include <iostream>
#include <sstream>
#include <string>

int main()
{
  using namespace std;
  using namespace std::chrono;
  // Parse %j as number of days into a duration
  istringstream in{"222"};
  hours d;
  in >> parse("%j", d);
  cout << d << '\n';
  cout << format("{:%j}", d) << '\n';
}
